import React, {useState,useEffect} from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const Home=()=>{
    const [data,setData]=useState([])
    const loadData = async ()=>{
        const response = await axios.get("http://localhost:3002/api/get")
        setData(response.data)
    }
    useEffect(()=>{
        loadData()},
        []
    )
    const deleteIdx = (P_Name)=>{

        axios.delete(`http://localhost:3002/api/delete/${P_Name}`)
        setTimeout(() => loadData(),500)

    }
    return(

        <div style={{margin: "150px" }}>
            <Link to ={"/add"}>
            <button className="btn btn-add">Add</button>
            </Link>
            <table className="styled-table">
                <thead>
                    <tr>
                        <th style={{textAlign:"center" }}>S.No</th>
                        <th style={{textAlign:"center" }}>Name</th>
                        <th style={{textAlign:"center" }}>Country</th>
                        <th style={{textAlign:"center" }}>Footprint</th>
                    </tr>

                </thead>
                <tbody>
                    {data.map((item,index)=>{
                        return(
                            <tr key = {item.P_Name}>
                                <th scope="row">{index+1}</th>
                                <td>{item.P_Name}</td>
                                <td>{item.Country}</td>
                                <td>{item.Footprint}</td>
                                <td>
                                    <Link to={`/edit/${item.P_Name}`}>
                                        <button className="btn btn-edit">Edit</button>
                                    </Link>
                                    <button className="btn btn-delete" onClick={()=>deleteIdx(item.P_Name)}>Delete</button>
                                    <Link to={`/view/${item.P_Name}`}>
                                        <button className="btn btn-view">View</button>
                                    </Link>

                                </td>
                            </tr>
                        )
                    }

                    )}
                </tbody>
            </table>

        </div>
    );
}

export default Home;
