import axios from "axios";
import React,{useState} from "react";
import { Link, useNavigate  } from "react-router-dom";
import { toast } from "react-toastify";
const initialState={
    p_name:"",
    country:"",
    family:"",
    electricity:"",
    lpg:"",
    diesel:"",
    petrol:"",
    d2w:""

}
const Add =()=>{
    const [state,setState]=useState(initialState)
    const navigate=useNavigate()
    const {p_name,country,family,electricity,lpg,diesel,petrol,d2w}=state
    const handleSubmit=(e)=>{
        e.preventDefault()
        axios.post("http://localhost:3002/api/post",
        {p_name,country,family,electricity,lpg,diesel,petrol,d2w}
        ).then(()=>{
            setState({p_name:"",
            country:"",
            family:"",
            electricity:"",
            lpg:"",
            diesel:"",
            petrol:"",
            d2w:""})
        }).catch((e)=>toast.error(e.response.data))
        setTimeout(()=>history.push("/"),500)
        }    
    const handleInputChange=(e)=>{
        const {name,value}=e.target;
        setState({...state,[name]:value})
    }
    return(
        <div style={{marginTop: "100px"}}>
            <form style={{
                margin: "auto",
                padding: "15px",
                maxWidth: "400px",
                alignContent: "center"
            }}
            onSubmit={handleSubmit}>
                <label htmlFor="name">Name</label>
                <input type="text" id="name" name="name" placeholder="Your name..." value={p_name} onChange={handleInputChange}></input>
                <label htmlFor="country">Country</label>
                <input type="text" id="country" name="country" placeholder="Your country..." value={country} onChange={handleInputChange}></input>
                <label htmlFor="family">Family Count</label>
                <input type="number" id="family" name="family" placeholder="Your family..." value={family} onChange={handleInputChange}></input>
                <label htmlFor="electricity">Electricity</label>
                <input type="number" id="electricity" name="electricity" placeholder="Your KWh..." value={electricity} onChange={handleInputChange}></input>
                <label htmlFor="lpg">LPG</label>
                <input type="number" id="lpg" name="lpg" placeholder="Your lpg..." value={lpg} onChange={handleInputChange}></input>
                <label htmlFor="diesel">Dist Diesel</label>
                <input type="number" id="diesel" name="diesel" placeholder="Your diesel dist..." value={diesel} onChange={handleInputChange}></input>
                <label htmlFor="petrol">Dist Petrol</label>
                <input type="number" id="petrol" name="petrol" placeholder="Your petrol dist..." value={petrol} onChange={handleInputChange}></input>
                <label htmlFor="d2w">Dist_2wh</label>
                <input type="number" id="d2w" name="d2w" placeholder="Your 2wheeler dist..." value={d2w} onChange={handleInputChange}></input>
                <input type ="submit" value="save"></input>
                <Link to={"/"}>
                    <input type="button" value="go-back"></input>
                </Link>                
            </form>
        </div>
    )
}
export default Add;
