import axios from "axios";
import React,{useState, useRef} from "react";
import { Link, useNavigate  } from "react-router-dom";
import { toast } from "react-toastify";
const initialState={
    P_Name:"", Country:"", Family_Count:0, Electricity:0, LPG:0, Dist_Car_Diesel:0, Dist_Car_Petrol:0, Dist_2wh:0

}
const Add =()=>{
    const ref = useRef(null)
    const [state,setState]=useState(initialState)
    const navigate=useNavigate()
    const {P_Name, Country, Family_Count, Electricity, LPG, Dist_Car_Diesel, Dist_Car_Petrol, Dist_2wh }=state
    const handleSubmit=(e)=>{
        console.log(ref.current.value)
        e.preventDefault()
        axios.post("http://localhost:3002/api/post",
        {P_Name, Country, Family_Count, Electricity, LPG, Dist_Car_Diesel, Dist_Car_Petrol, Dist_2wh}
        ).then(()=>{
            setState({P_Name:ref.current.value, Country:ref.current.value, Family_Count:ref.current.value, Electricity:ref.current.value, LPG:ref.current.value, Dist_Car_Diesel:ref.current.value, Dist_Car_Petrol:ref.current.value, Dist_2wh:ref.current.value})
        }).catch((e)=>toast.error(e.response.data))
        setTimeout(()=>navigate("/"),500)
        }    

    return(
        <div style={{marginTop: "100px"}}>
            <form style={{
                margin: "auto",
                padding: "15px",
                maxWidth: "400px",
                alignContent: "center"
            }}
            onSubmit={handleSubmit}>
                <label htmlFor="name">Name</label>
                <input ref ={ref} type="text" id="name" name="name" placeholder="Your name..." defaultValue={P_Name} ></input>
                <label htmlFor="country">Country</label>
                <input  ref ={ref} type="text" id="country" name="country" placeholder="Your country..." defaultValue={Country} ></input>
                <label htmlFor="family">Family Count</label>
                <input  ref ={ref} type="number" id="family" name="family" placeholder="Your family..." defaultValue={Family_Count} ></input>
                <label htmlFor="electricity">Electricity</label>
                <input  ref ={ref} type="number" id="electricity" name="electricity" placeholder="Your KWh..." defaultValue={Electricity} ></input>
                <label htmlFor="lpg">LPG</label>
                <input  ref ={ref} type="number" id="lpg" name="lpg" placeholder="Your lpg..." defaultValue={LPG} ></input>
                <label htmlFor="diesel">Dist Diesel</label>
                <input  ref ={ref} type="number" id="diesel" name="diesel" placeholder="Your diesel dist..." defaultValue={Dist_Car_Diesel} ></input>
                <label htmlFor="petrol">Dist Petrol</label>
                <input  ref ={ref} type="number" id="petrol" name="petrol" placeholder="Your petrol dist..." defaultValue={Dist_Car_Petrol} ></input>
                <label htmlFor="d2w">Dist_2wh</label>
                <input  ref ={ref}type="number" id="d2w" name="d2w" placeholder="Your 2wheeler dist..." defaultValue={Dist_2wh} ></input>
                <input type ="submit" value="save"></input>
                <Link to={"/"}>
                    <input type="button" value="go-back"></input>
                </Link>                
            </form>
        </div>
    )
}
export default Add;
